        }
        for(int i = 0; i < h; i++){
            for(int j = 0; j < w; j++){
                if(field[i][j] == 'B'){
                    dfs(i, j, 1);
                }else if(field[i][j] == 'W'){
                    dfs(i, j, 2);
                }
            }
