#include <bits/stdc++.h>
using namespace std;
using lint = long long;
template<class T = int> using V = vector<T>;
template<class T = int> using VV = V< V<T> >;

struct UnionFind {
  const int n;
  V<> t; // root ? -sz : par
  UnionFind(int n) : n(n), t(n, -1) {}
  int find(int v) { return t[v] < 0 ? v : t[v] = find(t[v]); }
  void unite(int u, int v) {
    if ((u = find(u)) == (v = find(v))) return;
    if (-t[u] < -t[v]) swap(u, v);
    t[u] += t[v];
    t[v] = u;
  }
  bool same(int u, int v) { return find(u) == find(v); }
  int size(int v) { return -t[find(v)]; }
};

int main() {
  cin.tie(nullptr); ios::sync_with_stdio(false);
  while (true) {
    int w, h; cin >> w >> h;
    if (!w) break;
    V<string> s(h); for (auto&& e : s) cin >> e;
    V<> di{1, 0, -1, 0}, dj{0, 1, 0, -1};
    auto in = [&](int i, int j) -> bool { return 0 <= i and i < h and 0 <= j and j < w; };
    auto _ = [&](int i, int j) -> int { return i * w + j; };
    UnionFind uf(h * w);
    for (int i = 0; i < h; ++i) for (int j = 0; j < w; ++j) if (s[i][j] == '.') {
      if (i + 1 < h and s[i + 1][j] == '.') uf.unite(_(i, j), _(i + 1, j));
      if (j + 1 < w and s[i][j + 1] == '.') uf.unite(_(i, j), _(i, j + 1));
    }
    set<int> sb, sw;
    for (int i = 0; i < h; ++i) for (int j = 0; j < w; ++j) {
      if (s[i][j] == 'B') {
        for (int d = 0; d < 4; ++d) {
          int ni = i + di[d], nj = j + dj[d];
          if (!in(ni, nj)) continue;
          if (s[ni][nj] == '.') sb.insert(uf.find(_(ni, nj)));
        }
      }
      if (s[i][j] == 'W') {
        for (int d = 0; d < 4; ++d) {
          int ni = i + di[d], nj = j + dj[d];
          if (!in(ni, nj)) continue;
          if (s[ni][nj] == '.') sw.insert(uf.find(_(ni, nj)));
        }
      }
    }
    int cb = 0, cw = 0;
    for (int i = 0; i < h; ++i) for (int j = 0; j < w; ++j) if (s[i][j] == '.') {
      int p = uf.find(_(i, j));
      cb += sb.count(p) and !sw.count(p);
      cw += sw.count(p) and !sb.count(p);
    }
    cout << cb << ' ' << cw << '\n';
  }
}

