void insert_stack(std::vector<std::vector<char> > &map, std::stack<std::pair<int, int> > &st, char self_color, char other_color, int &count, bool &check_surrounded, bool &check_only_wall); 
void dfs(std::vector<std::vector<char> > &map, int i, int j, char self_color, char other_color, int &total); 
int whole_search(std::vector<std::vector<char> > map, int w, int h, char self_color, char other_color); 

int main(void) {
    int w;
    int h;

    while (1) {
        std::cin >> w >> h;
        if (w == 0 && h == 0) {
            break;
        }

        std::vector<std::vector<char> > map_b(h+2, std::vector<char>(w+2, '#'));
        std::vector<std::vector<char> > map_w(h+2, std::vector<char>(w+2, '#'));

        for (int i = 1; i <= h; i++) {
            for (int j = 1; j <= w; j++) {
                std::cin >> map_b[i][j];
                map_w[i][j] = map_b[i][j];
            }
        }
        std::cout << whole_search(map_b, w, h, 'B', 'W') << " " << whole_search(map_w, w, h, 'W', 'B') << std::endl;
    }


    return 0;
}

void insert_stack(std::vector<std::vector<char> > &map, std::stack<std::pair<int, int> > &st, char self_color, char other_color, int &count, bool &check_surrounded, bool &check_only_wall) {
    int dx[] = {0, 0, 1, -1};
    int dy[] = {1, -1, 0, 0};
    int first;
    int second;

    first = st.top().first;
    second = st.top().second;
    st.pop();
    ++count;

    for (int k = 0; k < 4; k++) {
        if (map[first + dx[k]][second + dy[k]] == '.') {
            st.push(std::make_pair(first + dx[k], second + dy[k])); 
            map[first + dx[k]][second + dy[k]] = '$';
        } else if (map[first + dx[k]][second + dy[k]] == other_color) {
            check_surrounded = false; 
        } else if (map[first + dx[k]][second + dy[k]] == self_color) {
            check_only_wall = true;
        }
    }
    return;
}

void dfs(std::vector<std::vector<char> > &map, int i, int j, char self_color, char other_color, int &total) {
    int count = 0;
    bool check_surrounded = true;
    bool check_only_wall = false;

    std::stack<std::pair<int, int> > st;
    st.push(std::make_pair(i, j));
    map[i][j] = '$';

    while (1) {
        if (st.empty() == 1) {
            break; 
        }
        insert_stack(map, st, self_color, other_color, count, check_surrounded, check_only_wall);
    }

    if (check_surrounded == true && check_only_wall == true) {
        total += count;
    } else {
        check_surrounded = true;
        check_only_wall = false;
    }
    return;
}

int whole_search(std::vector<std::vector<char> > map, int w, int h, char self_color, char other_color) {
    int total = 0;

    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            if (map[i][j] == '.') {
                dfs(map, i, j, self_color, other_color, total);
            }
        }
    }
    return total; 
}

