int dfs(vector<vector<int>> &m, int h, int w, vector<vector<char>> as) {
    if (h < 0 || w < 0 || h >= m.size() || w >= m[h].size()) {
        return 0;
    }
    if (as[h][w] == 'B') return 1;
    if (as[h][w] == 'W') return 2;
    if (f[h][w]) return 0;
    f[h][w] = 1;
    int t=0;
    t |= dfs(m, h-1, w, as);
    t |= dfs(m, h+1, w, as);
    t |= dfs(m, h, w+1, as);
    t |= dfs(m, h, w-1, as);
    return t;
}

