        for(int i=0; i< h; i++) cin >> field[i];

        int a = 0, b = 0;
        for(int i=0; i < h; i++){
            for(int j=0; j < w; j++){
                if(field[i][j] == '.'){
                    int r = check(i, j);
                    if(r == 1) a++;
                    else if(r == 2) b++;
                }
            }
        }
        cout << b << " " << a << endl;
    }
}

