int main(){
  vector<P> ans;
  while(1){
    int h,w;
    cin>>h>>w;
    swap(h,w);
    if(h==0&&w==0) break;
    int grid[55][55]={0};
    for(int i=0;i<h;i++){
      for(int j=0;j<w;j++){
        char a;
        cin>>a;
        if(a=='W') grid[i][j]=1;
        else if(a=='B') grid[i][j]=2;
      }
    }
    int bcnt=0,wcnt=0;
    for(int i=0;i<h;i++){
      for(int j=0;j<w;j++){
