          bool used[55][55];
          for(int k=0;k<55;k++){
            for(int l=0;l<55;l++) used[k][l]=false;
          }
          bool black=false,white=false;
          queue<P> que;
          que.push(P(i,j));
          used[i][j]=true;
          while(!que.empty()){
            P pos=que.front();
            que.pop();
            int x=pos.first,y=pos.second;
