void dfs(int y, int x, char c, vector<string> &vs) {
  if(y < 0 || y >= h || x < 0 || x >= w) return;
  if(vs[y][x] == 'W' || vs[y][x] == 'B') return;
  if(vs[y][x] == c || vs[y][x] == '3') return;
  if(vs[y][x] == '.') vs[y][x] = c;
  else if(vs[y][x] != c) vs[y][x] = '3';
  dfs(y+1,x,c,vs);
  dfs(y-1,x,c,vs);
  dfs(y,x+1,c,vs);
  dfs(y,x-1,c,vs);
}
