const int dw[4]={0,1,0,-1};

int w,h,cnt,surrounded,x,y,nh,nw,ansb,answ;
vector<string> a(MAX_H);
vector<vector<int>> visited(MAX_H,vector<int>(MAX_W));
queue<pair<int,int>> que;

int main(){
    cin.tie(0);
    ios::sync_with_stdio(false);
    while (cin >> w >> h,w){
        for (int i=0;i<h;++i) cin >> a[i];
        for (int i=0;i<h;++i)
            for (int j=0;j<w;++j)
                visited[i][j]=0;
        ansb=answ=0;
        for (int i=0;i<h;++i){
            for (int j=0;j<w;++j){
                if (a[i][j]!='.') continue;
                if (visited[i][j]) continue;
                cnt=0,surrounded=0;
                visited[i][j]=1;
                que.emplace(i,j);
                while(!que.empty()){
                    ++cnt;
                    auto p=que.front(); que.pop();
                    x=p.first,y=p.second;
                    for (int k=0;k<4;++k){
                        nh=x+dh[k],nw=y+dw[k];
