                        if (a[nh][nw]=='B'){surrounded|=1; continue;}
                        if (a[nh][nw]=='W'){surrounded|=2; continue;}
                        visited[nh][nw]=1;
                        que.emplace(nh,nw);
                    }
                }
                if (surrounded==1) ansb+=cnt;
                if (surrounded==2) answ+=cnt;
            }
        }
