					flag[p2.y][p2.x] = true;
					dfs(p2, flag);
				}
			}
		};
		rep(i, h)rep(j, w) {
			if (s[i][j] == 'B') {
				dfs(Point(j, i), B);
			} else if (s[i][j] == 'W') {
				dfs(Point(j, i), W);
			}
		}

		int cntB = 0, cntW = 0;
		rep(i, h)rep(j, w)if (s[i][j] == '.') {
			if (B[i][j] && !W[i][j]) {
				cntB++;
			} else if (!B[i][j] && W[i][j]) {
				cntW++;
			}
		}
		out(cntB, cntW);
	}
}

#include <iostream>
