using namespace std;

int w, h;

void dfs(vector< vector<int> >& area, int x, int y, bool flag)
{
	if (flag) {
		area[x][y] ^= 4;
		if (x - 1 >= 0 && !(area[x - 1][y] & 2) && !(area[x - 1][y] & 4)) {
			dfs(area,x - 1,y,flag);
		}
		if (y - 1 >= 0 && !(area[x][y - 1] & 2) && !(area[x][y - 1] & 4)) {
			dfs(area, x, y - 1, flag);
		}
		if (x + 1 < w && !(area[x + 1][y] & 2) && !(area[x + 1][y] & 4)) {
			dfs(area, x + 1, y, flag);
		}
		if (y + 1 < h && !(area[x][y + 1] & 2) && !(area[x][y + 1] & 4)) {
			dfs(area, x, y + 1, flag);
		}
	}
	else {
		area[x][y] ^= 8;
		if (x - 1 >= 0 && !(area[x - 1][y] & 1) && !(area[x - 1][y] & 8)) {
			dfs(area, x - 1, y, flag);
		}
		if (y - 1 >= 0 && !(area[x][y - 1] & 1) && !(area[x][y - 1] & 8)) {
			dfs(area, x, y - 1, flag);
		}
		if (x + 1 < w && !(area[x + 1][y] & 1) && !(area[x + 1][y] & 8)) {
			dfs(area, x + 1, y, flag);
		}
		if (y + 1 < h && !(area[x][y + 1] & 1) && !(area[x][y + 1] & 8)) {
			dfs(area, x, y + 1, flag);
		}
	}
}

