	while (cin >> w >> h, w || h) {
		vector< vector<int> > area(w, vector<int>(h));
		for (int i = 0; i < h; i++) {
			for (int j = 0; j < w; j++) {
				cin >> ch;
				if (ch == 'B') {
					area[j][i] = 1;
				}
				else if (ch == 'W') {
					area[j][i] = 2;
				}
			}
		}
		for (int i = 0; i < h; i++) {
			for (int j = 0; j < w; j++) {
				if (area[j][i] & 1) {
					dfs(area, j, i, true);
				}
