	{
	  for(int j=0;j<w;j++)
	    {
	      if(field[i][j]=='W')
		{
		  solve(j,i,'W');
		}
	      else if(field[i][j]=='B')
		{
		  solve(j,i,'B');
		}
	    }
	}
      int blackcount=0,whitecount=0;
      for(int i=0;i<h;i++)
	{
	  for(int j=0;j<w;j++)
	    {
	      if(black[i][j] && !white[i][j]) blackcount++;
	      else if(!black[i][j] && white[i][j]) whitecount++;
	    }
