int w, h, white, black;
char a[52][52];
int dx[4] = {0, 0, -1, 1};
int dy[4] = {-1, 1, 0, 0};

void dfs(int x, int y, char c){
	for(int i=0 ; i < 4 ; i++ ){
		int mx = x + dx[i], my = y + dy[i];
		if( mx < 0 || my < 0 || w <= mx || h <= my ) continue;

		if( c == 'W' ){
			if( a[y][x] == 'W' || a[y][x] == 'w' ){
				if( a[my][mx] == '.' ){
					a[my][mx] = 'w';
					dfs(mx, my, c);
				}
			}
		}else if( c == 'B' ){
			if( a[y][x] == 'B' || a[y][x] == 'b' ){
				if( a[my][mx] == 'w' || a[my][mx] == 'W'){
					if( a[y][x] == 'b' ){
						a[y][x] = '?';
					}
					if( a[my][mx] == 'w' ){
						a[my][mx] = '?';
					}
				}

				if( a[my][mx] == '.' ){
					a[my][mx] = 'b';
					dfs(mx, my, c);
				}
			}
		}else if( c == '?' ){
			if( a[y][x] == 'w' || a[y][x] == 'b' ){
				a[y][x] = '?';
			}
			if( a[my][mx] == 'w' || a[my][mx] == 'b' ){
				a[my][mx] = '?';
				dfs(mx, my, c);
			}
		}
	}
}

void solve(){
	for(int y=0 ; y < h ; y++ ){
		for(int x=0 ; x < w ; x++ ){
			if( a[y][x] == 'W' ){				
				dfs(x, y, 'W');
			}
		}
	}
	for(int y=0 ; y < h ; y++ ){
		for(int x=0 ; x < w ; x++ ){
			if( a[y][x] == 'B' ){
				dfs(x, y, 'B');
			}
		}
	}
	for(int y=0 ; y < h ; y++ ){
		for(int x=0 ; x < w ; x++ ){
			if( a[y][x] == '?' ){
				dfs(x, y, '?');
			}
		}
	}
	for(int y=0 ; y < h ; y++ ){
		for(int x=0 ; x < w ; x++ ){
			if( a[y][x] == 'w' ) white++;
			if( a[y][x] == 'b' ) black++;
		}
	}
}

int main(){
	while( cin >> w >> h , w || h ){
		for(int y=0 ; y < h ; y++ ){
			cin >> a[y];
		}
		white = black = 0;
		solve();
		cout << black << " " << white << endl;
	}
}

