    int w;
    int h;
    std::cin >> w >> h;

    std::vector<std::vector<char> > map_b(h+2, std::vector<char>(w+2, 'B'));
    std::vector<std::vector<char> > map_w(h+2, std::vector<char>(w+2, 'W'));

    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            std::cin >> map_b[i][j];
            map_w[i][j] = map_b[i][j];
        }
    }
    std::cout << dfs(map_b, w, h, 'W') << " " << dfs(map_w, w, h, 'B') << std::endl;


    return 0;
}

