constexpr size_t m1 = -1;
constexpr size_t di[] = {m1, 0, 1, 0};
constexpr size_t dj[] = {0, m1, 0, 1};

std::vector<size_t> make_vector(size_t n, size_t x) {
  return std::vector<size_t>(n, x);
}

template <class Tp>
std::vector<Tp> make_vector(size_t n, const Tp& x) {
  return std::vector<Tp>(n, x);
}

template <class... Ts>
auto make_vector(size_t n, size_t m, const Ts&... x) {
  return std::vector<decltype(make_vector(m, x...))>(n, make_vector(m, x...));
}


using zahyo = std::pair<size_t, size_t>;
void bfs(const std::vector<std::string>& a,
         std::queue<zahyo>& q, std::vector<std::vector<bool>>& b) {

  size_t h = a.size();
  size_t w = a[0].size();
  while (!q.empty()) {
    size_t i, j;
    std::tie(i, j) = q.front();
    q.pop();
    for (int k = 0; k < 4; ++k) {
      size_t ni = i + di[k];
      size_t nj = j + dj[k];
      if (!(ni < h && nj < w)) continue;
      if (a[ni][nj] != '.') continue;
      if (b[ni][nj]) continue;
      b[ni][nj] = true;
      q.emplace(ni, nj);
    }
  }
}

int testcase_ends() {
  size_t h, w;
  scanf("%zu %zu", &w, &h);
  if (h == 0 && w == 0) return 1;

  std::queue<zahyo> qw, qb;
  auto xb = make_vector(h, w, false);
  auto xw = xb;

  std::vector<std::string> a(h);
  for (size_t i = 0; i < h; ++i) {
    char buf[64];
    scanf("%s", buf);
    a[i] = buf;
    for (size_t j = 0; j < w; ++j) {
      if (a[i][j] == 'W') {
        qw.emplace(i, j);
        // xw[i][j] = true;
      } else if (a[i][j] == 'B') {
        qb.emplace(i, j);
        // xb[i][j] = true;
      }
    }
  }

  bfs(a, qw, xw);
  bfs(a, qb, xb);

  int black = 0;
  int white = 0;
  for (size_t i = 0; i < h; ++i) {
    for (size_t j = 0; j < w; ++j) {
      if (a[i][j] != '.') continue;
      if (xb[i][j] && xw[i][j]) continue;
      int c = 0;
      if (xb[i][j]) c = +1;
      if (xw[i][j]) c = -1;
      if (c == 0) continue;
      for (int k = 0; k < 4; ++k) {
        size_t ni = i + di[k];
        size_t nj = j + dj[k];
        if (!(ni < h && nj < w)) continue;
        if (c > 0 && a[ni][nj] == 'W') {
          c = 0;
          break;
        }
        if (c < 0 && a[ni][nj] == 'B') {
          c = 0;
          break;
        }
      }
      if (c > 0) ++black;
      if (c < 0) ++white;
    }
  }

  printf("%d %d\n", black, white);
  return 0;
}

int main() {
  while (!testcase_ends()) {}
}

