int w, h;

#define MAX 51
char brd[MAX][MAX];
bool done[MAX][MAX];
bool calc_done[MAX][MAX];

int dx[] = { 0, 0, -1, 1 };
int dy[] = { -1, 1, 0, 0 };

#define EMPTY 0
#define BLACK 1
#define WHITE 2

int only(int y, int x)
{
	if (!(0 <= y && y < h)) return EMPTY;
	if (!(0 <= x && x < w)) return EMPTY;
	if (done[y][x]) return EMPTY;
	done[y][x] = true;

	if (brd[y][x] == 'B')
		return BLACK;
	else if (brd[y][x] == 'W')
		return WHITE;

	int code = EMPTY;
	if (brd[y][x] == '.') {
		rep(i, 4) 
			code |= only(y+dy[i], x+dx[i]);
	}
	return code;
}

int calc(int y, int x)
{
	if (!(0 <= y && y < h)) return 0;
	if (!(0 <= x && x < w)) return 0;
	if (calc_done[y][x]) return 0;
	calc_done[y][x] = true;
	if (brd[y][x] != '.') return 0;

	int cnt = 1;
	rep(i, 4)
		cnt += calc(y+dy[i], x+dx[i]);
	return cnt;
}

int main(void)
{
	while (cin >> w >> h, w|h) {
		rep(j, h) rep(i, w) cin >> brd[j][i];

		int ww, bb;
		ww = bb = 0;

		memset(calc_done, 0, sizeof(calc_done));
		rep(j, h) {
			rep(i, w) {
				memset(done, 0, sizeof(done));

				int c = only(j, i);
				if ((c&BLACK) && (c&WHITE))
					continue;
				else if (c&BLACK) {
					bb += calc(j, i);
				} else if (c&WHITE) {
					ww += calc(j, i);
				}
			}
		}

		printf("%d %d\n", bb, ww);
	}
    return 0;
}

