#define rep(i,n) for(int i=0;i<n;i++)
#define repn(i,n) for(int i=1;i<=n;i++)

int n,m;
char x[55][55];
bool used[55][55][2];
int main(){
    while (1) {
        scanf("%d%d",&m,&n);
        if(n == 0) return 0;
        rep(i,n) scanf("%s",&x[i]);
        rep(i,n) rep(j,m) used[i][j][0] = used[i][j][1] = 0;
        int dx[4]={0,0,1,-1};
        int dy[4]={1,-1,0,0};
        queue<pair<int,int> >que[2];
        rep(i,n) rep(j,m){
            if(x[i][j] == '.'){
                rep(k,4){
                    int nx = i+dx[k];
                    int ny = j+dy[k];
                    if(0<=nx&&nx<n&&0<=ny&&ny<m){
                        if(x[nx][ny] == 'B'){
                            que[0].push(pair<int,int>(i,j)); //break;
                        }
                        if(x[nx][ny] == 'W'){
                            que[1].push(pair<int,int>(i,j)); //break;
                        }
                    }
                }
            }
        }
        rep(q,2){
            while(!que[q].empty()){
                pair<int,int>p = que[q].front(); que[q].pop();
                if(used[p.first][p.second][q]) continue;
                used[p.first][p.second][q] = 1;
                rep(k,4){
                    int nx = p.first+dx[k];
                    int ny = p.second+dy[k];
                    if(0<=nx&&nx<n&&0<=ny&&ny<m){
                        if(x[nx][ny] == '.'){
                            que[q].push(pair<int,int>(nx,ny));
                        }
                    }
                }
            }
        }
        int ans[2]={};
        rep(i,n)rep(j,m){
            if(used[i][j][0] && !used[i][j][1]) ans[0]++;
            if(used[i][j][1] && !used[i][j][0]) ans[1]++;
        }
        cout << ans[0] << " "  << ans[1] << endl;
    }
}

