int cked[2][60][60],own[2][60][60];
int ad(int y,int x,int c){
	cked[c][y][x]=1;
	int r=0,ny,nx;
	rep(d,4){
		ny=y+dy[d],nx=x+dx[d];
		if(!ck(ny,h)||!ck(nx,w)||cked[c][ny][nx])continue;
		if(land[ny][nx]==(c?'W':'B'))return 1;
	}
	rep(d,4){
		ny=y+dy[d],nx=x+dx[d];
		if(!ck(ny,h)||!ck(nx,w)||cked[c][ny][nx])continue;
		if(land[ny][nx]=='.')r=r||ad(ny,nx,c);
		if(r)return 1;
	}
	return r;
}
void fl(int y,int x,int c){
	own[c][y][x]=1;
	rep(d,4){
		int ny=y+dy[d],nx=x+dx[d];
		if(ck(ny,h)&&ck(nx,w)&&!own[c][ny][nx]&&land[ny][nx]=='.')fl(ny,nx,c);
	}
}
