State dfs(int y, int x) {
    if (y < 0 || y >= h || x < 0 || x >= w) return { 0, 0, 0 };
    if (A[y][x] == 'B') return { 1, 0, 0 };
    if (A[y][x] == 'W') return { 0, 1, 0 };
    if (vis[y][x]) return { 0, 0, 0 };
    vis[y][x] = 1;
    if (A[y][x] == '.') {
        State sum = { 0, 0, 1 };
        loop (4, i) sum = sum + dfs(y + dir[i], x + dir[i + 1]);
        return sum;
    }
}

