        loop (h, i) loop (w, j) {
            auto rt = dfs(i, j);
            if (rt.b && rt.w) continue;
            if (rt.b) ans_b += rt.empty;
            if (rt.w) ans_w += rt.empty;
        }

        cout << ans_b << " " << ans_w << endl;
    }
    return 0;
}

