#include <bits/stdc++.h>
using namespace std;

int h, w;
char ground[52][52];
int dx[4] = {1, 0, -1, 0};
int dy[4] = {0, 1, 0, -1};

int BFS(int i, int j){
    int isvisited[52][52] = {};
    bool B = false;
    bool W = false;
    queue<pair<int, int>> que;
    que.push(pair<int, int>(i, j));
    while(!que.empty()){
        pair<int, int> q = que.front(); que.pop();
        for(int k=0;k<4;k++){
            pair<int, int> next_q;
            next_q.first = q.first + dx[k];
            next_q.second = q.second + dy[k];
            if(0 <= next_q.first && next_q.first < h &&
            0 <= next_q.second && next_q.second < w &&
            isvisited[next_q.first][next_q.second] == 0){
                isvisited[next_q.first][next_q.second] = 1;
                if(ground[next_q.first][next_q.second] == 'B'){
                    B = true;
                }
                else if(ground[next_q.first][next_q.second] == 'W'){
                    W = true;
                }
                else{
                    que.push(next_q);
                }
            }
        }
    }
    if(B && !W){
        return 1;
    }
    else if(!B && W){
        return -1;
    }
    else{
        return 0;
    }
}

int main(){
    while(true){
        cin >> w >> h;
        if(h == 0 && w == 0) break;
        int B = 0;
        int W = 0;
        for(int i=0;i<h;i++){
            for(int j=0;j<w;j++){
                cin >> ground[i][j];
            }
        }
        for(int i=0;i<h;i++){
            for(int j=0;j<w;j++){
                if(ground[i][j] == '.'){
                    int result = BFS(i, j);
                    if(result == 1){
                        B++;
                    }
                    if(result == -1){
                        W++;
                    }
                }
            }
        }
        cout << B << " " << W << "\n";
    }
    return 0;
}

