        for(int i = 0; i < H; i++){
            for(int j = 0; j < W; j++){
                if(landmap[i][j] == 'w') white_land++;
                else if(landmap[i][j] == 'b') black_land++;
            }
        }
        cout << black_land << ' ' << white_land << endl;
    }
    return 0;
}

void check_white(int x, int y)
{
    for(int i = 0; i < 4; i++){
        nx = x + dx[i];
        ny = y + dy[i];
        if(nx < 0 || ny < 0 || nx >= H || ny >= W) continue;
        else {
            if(issearched[nx][ny] == 0){
                issearched[nx][ny] = 1;
                if(landmap[nx][ny] == '.'){
                    landmap[nx][ny] = 'w';
                    check_white(nx, ny);
                }
                 // else if(map[nx][ny] == 'w'){
                    // check_white(nx, ny);
                // }
            }
        }
    }
    return;
}

void check_black(int x, int y)
{
    for(int i = 0; i < 4; i++){
        nx = x + dx[i];
        ny = y + dy[i];
        if(nx < 0 || ny < 0 || nx >= H || ny >= W) continue;
        else {
            if(issearched[nx][ny] == 0){
                issearched[nx][ny] = 1;
                if(landmap[nx][ny] == '.'){
                    landmap[nx][ny] = 'b';
                    check_black(nx, ny);
                } else if(landmap[nx][ny] == 'w'){
                    landmap[nx][ny] = '.';
                    check_black(nx, ny);
                }
            }
        }
    }
    return;
}

