void solve(){
  int black = 0, white = 0;

  REP(i, h){
    REP(j, w){
      scanf(" %c", &map[j][i]);
    }
  }

  REP(i, h){
    REP(j, w){
      if(map[j][i] == 'B'){
	check(j, i, 1);
      }
      else if(map[j][i] == 'W'){
	check(j, i, 2);
      }
    }
  }

  REP(i, h){
    REP(j, w){
      if(map[j][i] == 1) black++;
      else if(map[j][i] == 2) white++;
    }
  }
