  REP(i, 4){
    nx = x + dx[i];
    ny = y + dy[i];
    if(nx >= 0 && nx < w && ny >= 0 && ny < h){
      char point = map[nx][ny];
      if(point != 'B' && point != 'W'){
	if(map[nx][ny] == '.'){
	  map[nx][ny] = mark;
	  check(nx, ny, mark);
	}
	else{ 
	    map[nx][ny] |= mark;
	    if((mark & point) != mark) check(nx, ny, mark);
	}
      }
    }
  }
}

