bool solve(int x, int y, int c){
  if(!(0<=x && x<h && 0<=y && y<w))return true;
  if(a[x][y]==c || a[x][y]==c*2)return true;
  if(a[x][y]!=0)return false;
  bool flag=true;
  a[x][y]=c*2;
  for(int i=0; i<4; i++){
    int nx=x+dx[i], ny=y+dy[i];
    if(!solve(nx, ny, c)){
      flag=false;
      break;
    }
  }
  if(!flag){
    a[x][y]=INF;
    return false;
  }
  else return true;
}

