using namespace std;
using vec = vector<int>;
using mat = vector<vec>;
using lst = list<int>;

vec parent;
vec color;

int find(int i)
{
    if(i==parent[i])return i;
    return parent[i]=find(parent[i]);
}

void unite(int i,int j)
{
    i=find(i);
    j=find(j);
    parent[i]=j;
    color[j]=color[i]|color[j];
}

bool solve()
{
    int w,h;
    cin >> w >> h;
    if(w==0)return false;

    parent=vec(w*h);
    color=vec(w*h);

    for(int i=0;i<w*h;++i)
    {
        parent[i]=i;
        color[i]=0;
    }

    mat Tochi(h,vec(w));
    for(int i=0;i<h;++i)
    {
        string s;
        cin >> s;
        for(int j=0;j<w;++j)
        {
            char c = s[j];
            if(c=='.')Tochi[i][j]=0;
            else if(c=='B')Tochi[i][j]=1;
            else if(c=='W')Tochi[i][j]=2;

            if(i!=0)
            {
                if(Tochi[i][j]==0&&Tochi[i-1][j]!=0)
                {
                    color[i*w+j]=color[i*w+j] | Tochi[i-1][j];
                }
                else if(Tochi[i][j]!=0&&Tochi[i-1][j]==0)
                {
                    color[(i-1)*w+j]=color[(i-1)*w+j] | Tochi[i][j];
                }
            }
            if(j!=0)
            {
                if(Tochi[i][j]==0&&Tochi[i][j-1]!=0)
                {
                    color[i*w+j]=color[i*w+j] | Tochi[i][j-1];
                }
                else if(Tochi[i][j]!=0&&Tochi[i][j-1]==0)
                {
                    color[i*w+j-1]=color[i*w+j-1] | Tochi[i][j];
                }
            }
        }
    }

    for(int i=0;i<h;++i)
    {
        for(int j=0;j<w;++j)
        {
            if(i!=0&&Tochi[i][j]==0&&Tochi[i-1][j]==0)
            {
                unite(i*w+j,(i-1)*w+j);
            }
            if(j!=0&&Tochi[i][j]==0&&Tochi[i][j-1]==0)
            {
                unite(i*w+j,i*w+j-1);
            }
        }
    }

    int ansB=0,ansW=0;
    for(int i=0;i<w*h;++i)
    {
        ansB += color[find(i)]==1;
        ansW += color[find(i)]==2;
    }

    cout << ansB << " " << ansW << endl;

    return true;
}

int main()
{
    while(solve());
    return 0;
}

