typedef struct UnionFindTree{
    vector<int> par, bl, wh;

    UnionFindTree(int n){
        par.resize(n, -1);
        bl.resize(n, 0);
        wh.resize(n, 0);
    }

    bool is_root(int x){
        return par[x]<0;
    }

    int find(int x){
        if(is_root(x)) return x;
        return par[x] = find(par[x]);
    }

    int size(int x){
        return -par[find(x)];
    }

    bool unite(int x, int y){
        x = find(x);
        y = find(y);
        if(x == y) return false;
        if(size(x) < size(y)) swap(x, y);
        par[x] += par[y];
        bl[x] |= bl[y];
        wh[x] |= wh[y];
        par[y] = x;
        return true;
    }

    bool same(int x, int y){
        return find(x) == find(y);
    }

    void set_col(int x, int c){
        x = find(x);
        if(c == 0) bl[x] = 1;
        else wh[x] = 1;
    }
}UF;

