int dy[4] = {1, 0, -1, 0};

int idx(int x, int y){
    return x*w+y;
}

int main(){
    while(scanf("%d%d", &w, &h) && h){
        char a[h][w];
        rep(i, h)rep(j, w) scanf(" %c", &a[i][j]);
        UF uf(h*w);
        rep(x, h)rep(y, w)if(a[x][y]=='.'){
            rep(i, 4){
                int nx = x+dx[i], ny = y+dy[i];
                if(0<=nx && nx<h && 0<=ny && ny<w){
                    if(a[nx][ny] == 'B') uf.set_col(idx(x, y), 0);
                    else if(a[nx][ny] == 'W') uf.set_col(idx(x, y), 1);
                    else uf.unite(idx(x, y), idx(nx, ny));
                }
            }
        }
        int ansb = 0, answ = 0;
        rep(i, h*w)if(uf.is_root(i)){
            if(uf.bl[i] && !uf.wh[i]) ansb += uf.size(i);
            if(!uf.bl[i] && uf.wh[i]) answ += uf.size(i);
        }
        printf("%d %d\n", ansb, answ);
    }
}

