int w, h, cntw, cntb;
bool used[2][50][50];
char mas[50][50];
void dfs(int y,int x,bool wb){
  if(mas[y][x] != '.' || used[wb][y][x]++) return;
  for(int i = 0 ; i < 4 ; i++ ) dfs( y + dy[i], x + dx[i], wb);
}
int main(){
  while(cin >> w >> h , w){
    fill_n(**used,5000,0);
    cntw = cntb = 0;
    for(int i = 0 ; i < h ; i++ ){
      for(int j = 0 ; j < w ; j++ ) cin >> mas[i][j];
    }
    for(int i = 0 ; i < h ; i++ ) for(int j = 0 ; j < w ; j++ ){
      if(mas[i][j] == 'W') for(int k = 0 ; k < 4 ; k++ ) dfs(i+dy[k],j+dx[k],1);
      if(mas[i][j] == 'B') for(int k = 0 ; k < 4 ; k++ ) dfs(i+dy[k],j+dx[k],0);
    }
    for(int i = 0 ; i < h ; i++ ){
      for(int j = 0 ; j < w ; j++ ){
        if(used[0][i][j] && !used[1][i][j]) cntw++;
        else if(!used[0][i][j] && used[1][i][j]) cntb++;
      }
    }
    cout << cntw << " " << cntb << endl;
  }
}

#include <iostream>
#include <algorithm>

using namespace std;

int W, H;
int col, cnt;
char M[50][50];
bool used[50][50];

#define BLACK (1)
#define WHITE (2)

int in(int x, int y) {
  return 0 <= x && x < W && 0 <= y && y < H;
}

void dfs(int x, int y) {
  // 周りが一つの色かフィールド外だけで構成されると領域にカウント
  if(M[y][x] != '.') return;
  used[y][x] = true;
  cnt ++;

  const int dx[] = {-1, 1, 0, 0};
  const int dy[] = {0, 0, -1, 1};

  for(int i=0; i<4; i++) {
    int nx = x + dx[i], ny = y + dy[i];
    if(in(nx, ny) && !used[ny][nx]) {
      if(M[ny][nx] == 'B') col |= BLACK;
      if(M[ny][nx] == 'W') col |= WHITE;
      if(M[ny][nx] == '.') dfs(nx, ny);
    }
  }
}

int main() {

  while(cin >> W >> H && W) {
    for(int i=0; i<H; i++)
      for(int j=0; j<W; j++)
	cin >> M[i][j];

    fill(used[0], used[0]+2500, false);

    int res[4] = {};
    for(int i=0; i<H; i++)
      for(int j=0; j<W; j++)
	if(!used[i][j]) {
	  col = cnt = 0;
	  dfs(j, i);
	  res[col] += cnt;
	}

    cout << res[BLACK] << " " << res[WHITE] << endl;
  }

  return 0;
}

#include <string>
#include <vector>
#include <iostream>

using namespace std;

int W, H; vector<string> M;

const vector<int> dx = { 0, 1, 0, -1 };
const vector<int> dy = { -1, 0, 1, 0 };

int rec(int x, int y, vector<vector<bool> > ok)
{
	if (M[y][x] == 'W')
	{
		return 2;
	}

	if (M[y][x] == 'B')
	{
		return 1;
	}

	int ret = 3;

	for (int dir = 0; dir < 4; dir++)
	{
		int tx = x + dx[dir];
		int ty = y + dy[dir];

		if (0 <= tx && tx < W && 0 <= ty && ty < H && ok[ty][tx] == false)
		{
			vector<vector<bool> > ok2 = ok; ok2[ty][tx] = true;

			ret = ret & rec(tx, ty, ok2);
		}
	}

	return ret;
}

int main()
{
	int H, W;

	while (true)
	{
		cin >> W >> H;

		if (W == 0 && H == 0) { break; }

		M = vector<string>(H);

		for (int i = 0; i < H; i++)
		{
			cin >> M[i];
		}

		int b = 0, w = 0;

		for (int i = 0; i < H; i++)
		{
			for (int j = 0; j < W; j++)
			{
				int c = rec(j, i, vector<vector<bool> >(H, vector<bool>(W, false)));

				b += c & 1;
				w += (c & 2) / 2;
			}
		}

		cout << b << ' ' << w << endl;
	}

	return 0;
}

