#include<bits/stdc++.h>
using namespace std;

#define REP(i,n) for(int i=0;i<(n);i++)
//#define int long long

typedef pair<int, int> P;

int dx[4] = { 0,0,-1,1 }, dy[4] = { 1,-1,0,0 };
char A[55][55];
bool vis[55][55];
bool black[55][55], white[55][55];
int H, W;
void bfs_B(int x, int y) {
	queue<P>que;
	que.push({ x,y });
	REP(i, H)REP(j, W)vis[i][j] = false;
	while (!que.empty()) {
		P p = que.front(); que.pop();
		//cout<<p.first<<" "<<p.second<<" B"<<endl;
		REP(k, 4) {
			int nx = p.first + dx[k], ny = p.second + dy[k];
			if (nx >= 0 && nx<H&&ny >= 0 && ny<W && !vis[nx][ny] && A[nx][ny] == '.') {
				black[nx][ny] = true;
				que.push({ nx,ny });
				vis[nx][ny] = true;
			}
		}
	}
}
void bfs_W(int x, int y) {
	queue<P>que;
	que.push({ x,y });
	REP(i, H)REP(j, W)vis[i][j] = false;
	while (!que.empty()) {
		P p = que.front(); que.pop();
		//cout<<p.first<<" "<<p.second<<" W"<<endl;
		REP(k, 4) {
			int nx = p.first + dx[k], ny = p.second + dy[k];
			if (nx >= 0 && nx<H&&ny >= 0 && ny<W && !vis[nx][ny] && A[nx][ny] == '.') {
				white[nx][ny] = true;
				que.push({ nx,ny });
				vis[nx][ny] = true;
			}
		}
	}
}
void solve() {
	cin >> W >> H;
	if (W == 0)exit(0);
	REP(i, H) {
		REP(j, W) {
			cin >> A[i][j];
		}
	}
	REP(i, H)REP(j, W) {
		vis[i][j] = false;
		black[i][j] = false;
		white[i][j] = false;
	}
	// REP(i,H){
	//     REP(j,W)cout<<A[i][j];
	//     cout<<endl;
	// }
	REP(i, H) {
		REP(j, W) {
			if (A[i][j] == 'W')bfs_W(i, j);
			else if (A[i][j] == 'B')bfs_B(i, j);
		}
	}
	//cout << "done" << endl;
	int cB = 0, cW = 0;
	REP(i, H) {
		REP(j, W) {
			if (A[i][j] == '.'&&black[i][j] && !white[i][j]) {
				cB++;
			}
			else if (A[i][j] == '.' && !black[i][j] && white[i][j]) {
				cW++;
			}
		}
	}
	cout << cB << " " << cW << endl;
}

signed main() {
	while (true)solve();
}

