int main() {
    int w, h;
    while(cin >> w >> h, w|h){
        vector<vector<char>> v(h+2, vector<char>(w+2, -1));
        vector<string> g(h);
        for (int i = 0; i < h; ++i) {
            cin >> g[i];
            for (int j = 0; j < w; ++j) {
                v[i+1][j+1] = (g[i][j] != '.');
            }
        }
        queue<pair<int, int>> Q;
        array<int, 4> di = {1, -1, 0, 0}, dj = {0, 0, 1, -1};
        vector<vector<char>> visited(h, vector<char>(w, 0));

        int ansW = 0, ansB = 0;
        for (int i = 0; i < h; ++i) {
            for (int j = 0; j < w; ++j) {
                for (auto &&l : visited) {
                    fill(l.begin(),l.end(), 0);
                }
                int hitW = 0, hitB = 0;
                if(g[i][j] != '.') continue;
                Q.emplace(i, j);
                while(!Q.empty()){
                    int s, t; tie(s, t) = Q.front(); Q.pop();
                    visited[s][t] = 1;
                    for (int k = 0; k < 4; ++k) {
                        if(v[s+di[k]+1][t+dj[k]+1] == 0 && !visited[s+di[k]][t+dj[k]]){
                            Q.emplace(s+di[k], t+dj[k]);
                            visited[s+di[k]][t+dj[k]] = 1;
                        }else if(v[s+di[k]+1][t+dj[k]+1] == 1){
                            if(g[s+di[k]][t+dj[k]] == 'B') hitB = 1;
                            else hitW = 1;
                        }
                    }
                }
                if(hitW && !hitB) ansW++;
                if(hitB && !hitW) ansB++;
            }
        }
        cout << ansB << " " << ansW << "\n";
    }

    return 0;
}

