void dfsB(int y, int x){
	for(int i = 0; i < 4; i++){
		int nx = x + dx[i];
		int ny = y + dy[i];
		if(0 <= nx and nx < w and 0 <= ny and ny < h and bm[ny][nx] == 0 and maze[ny][nx] == '.'){
			bm[ny][nx] = 1;
			dfsB(ny, nx);
		}
	}
	return;
}

void dfsW(int y, int x){
	for(int i = 0; i < 4; i++){
