int w,h;
string in[50];
bool vis[50][50];

void solve(){
  rep(i,h)cin>>in[i];
  int bn=0,wn=0;

  rep(i,h)rep(j,w){
    if(in[i][j]!='.')continue;
    queue<PI> q;
    q.push(mp(i,j));
    bool bl=0,wh=0;
    memset(vis,0,sizeof(vis));
    while(!q.empty()){
      int cx=q.front().F,cy=q.front().S;
      q.pop();
      if(vis[cx][cy])continue;
      vis[cx][cy]=true;
      rep(k,4){
        int nx=cx+dx[k],ny=cy+dy[k];
        if(0>nx || nx>=h ||
           0>ny || ny>=w ||
           vis[nx][ny])continue;
        if(in[nx][ny]=='B'){
          bl=true;
          continue;
        }
        if(in[nx][ny]=='W'){
            wh=true;
            continue;
        }
        q.push(mp(nx,ny));
      }
    }

    char ch;
    if(bl && wh)ch=' ';
    else if(bl)ch='b';
    else if(wh)ch='w';
    else ch=' ';
    q=queue<PI>();
    q.push(mp(i,j));
    memset(vis,0,sizeof(vis));
    while(!q.empty()){
      int cx=q.front().F,cy=q.front().S;
      q.pop();
      if(vis[cx][cy])continue;
      in[cx][cy]=ch;
      vis[cx][cy]=true;
      rep(k,4){
        int nx=cx+dx[k],ny=cy+dy[k];
        if(0>nx || nx>=h ||
           0>ny || ny>=w ||
           vis[nx][ny] || in[nx][ny]!='.')continue;
        q.push(mp(nx,ny));
      }      
    }
  }
  rep(i,h)rep(j,w){
    if(in[i][j]=='b')++bn;
    else if(in[i][j]=='w')++wn;
  }
  cout<<bn<<' '<<wn<<endl;
}

main(){
  while(cin>>w>>h,w)
    solve();
}

