int t_y[] = {0, 1, 0, -1};

void search(int h, int w)
{
  char c = field[h][w];
  queue<p> que;
  que.push( p(h, w) );
  while ( !que.empty() ) {
    p n_c = que.front();
    //cout << n_c.first << " " << n_c.second << endl;
    que.pop(); 

    for ( int i = 0; i < 4; i++ ) {
      int t_h = n_c.first + t_x[i];
      int t_w = n_c.second + t_y[i];
      if ( field[t_h][t_w] == '#' ) continue;
      if ( field[t_h][t_w] == c || field[t_h][t_w] == '.' ) {
	if ( c == 'W') { 
	  if(!white[t_h][t_w]) {
	    white[t_h][t_w] = true;
	  } else {
	    continue;
	  }
	}
 	if (c == 'B') {
	  if (!black[t_h][t_w]) {
	    black[t_h][t_w] = true;
	  } else {
	    continue;
	  }
	}
	que.push( p(t_h, t_w) );
      }
    }
  }
}


