void wpaint(int a,int b){
	if(a>0 && map[a-1][b] == '.'){
		map[a-1][b] = 'w';
		wpaint(a-1,b);
	}
	if(b>0 && map[a][b-1] == '.'){
		map[a][b-1] = 'w';
		wpaint(a,b-1);
	}
	if(a<h && map[a+1][b] == '.'){
		map[a+1][b] = 'w';
		wpaint(a+1,b);
	}
	if(b<w && map[a][b+1] == '.'){
		map[a][b+1] = 'w';
		wpaint(a,b+1);
	}
}

void bpaint(int a,int b){
	if(a>0){
		if(map[a-1][b] == '.'){
			map[a-1][b] = 'b';
			bpaint(a-1,b);
		}else if(map[a-1][b] == 'w'){
			map[a-1][b] = 'g';
			bpaint(a-1,b);
		}
	}
	if(b>0){
		if(map[a][b-1] == '.'){
			map[a][b-1] = 'b';
			bpaint(a,b-1);
		}else if(map[a][b-1] == 'w'){
			map[a][b-1] = 'g';
			bpaint(a,b-1);
		}
	}
	if(a<h){
		if(map[a+1][b] == '.'){
			map[a+1][b] = 'b';
			bpaint(a+1,b);
		}else if(map[a+1][b] == 'w'){
			map[a+1][b] = 'g';
			bpaint(a+1,b);
		}
	}
	if(b<w){
		if(map[a][b+1] == '.'){
			map[a][b+1] = 'b';
			bpaint(a,b+1);
		}else if(map[a][b+1] == 'w'){
			map[a][b+1] = 'g';
			bpaint(a,b+1);
		}
	}
}

