				if(field[i][j] == 'W') {
					fw++;
				}
			}
		}
		REP(i, h) {
			REP(j, w) {
				if(field[i][j] == '.') {
					stack<pii> S;
					S.push(make_pair(i, j));
					vvi visited(h, vi(w, 0));
					bool black = false, white = false;
					while(!S.empty()) {
						pii p = S.top();
						S.pop();
						if(visited[p.first][p.second]) {
							continue;
						}
						visited[p.first][p.second] = 1;
						REP(d, 4) {
							int ny = p.first + dy[d];
							int nx = p.second + dx[d];
							if(ny<0 || h<=ny || nx<0 || w<=nx) {
								continue;
							} else if(field[ny][nx] == 'B') {
								black = true;							
							} else if(field[ny][nx] == 'W') {
								white = true;
							} else {
								S.push(make_pair(ny, nx));
							}
						}
					}
					if(black & white) {
						continue;
					} else if(black) {
						REP(i, h) {
							REP(j, w) {
								if(visited[i][j]) {
									field[i][j] = 'B';
								}
							}
						}
					} else if(white) {
						REP(i, h) {
							REP(j, w) {
								if(visited[i][j]) {
									field[i][j] = 'W';
								}
							}
						}
					}
				}
			}
		}
		int black = 0, white = 0;
		REP(i, h) {
			REP(j, w) {
				if(field[i][j] == 'B') {
					black++;
				}
				if(field[i][j] == 'W') {
					white++;
				}
			}
		}

		cout << black-fb << " " << white-fw << endl;
	}

	return 0;
}

