{
	island[cy][cx] = c;
	rep (k, 4 ){
		int nx = cx + dx[k];
		int ny = cy + dy[k];
		if (nx < 0 || nx >= w || ny < 0 || ny >= h || island[ny][nx] != '.' ) continue;
		dfs2 (nx, ny, c );
	} // end rep	
}

