int dfs(std::vector<std::vector<char> > map, int w, int h, char self_color, char other_color) {
    int dx[] = {0, 0, 1, -1};
    int dy[] = {1, -1, 0, 0};

    int first;
    int second;

    int total = 0;
    int count; 
    bool check_surrounded;
    bool check_only_wall;

    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            if (map[i][j] == '.') {
                count = 0;
                check_surrounded = true;
                check_only_wall = false;
                std::stack<std::pair<int, int> > st;
                st.push(std::make_pair(i, j));
                map[i][j] = '$';

                while (1) {
                    if (st.empty() == 1) {
                        break; 
                    }
                    first = st.top().first;
                    second = st.top().second;
                    st.pop();
                    ++count;
                    for (int k = 0; k < 4; k++) {
                        if (map[first + dx[k]][second + dy[k]] == '.') {
                            st.push(std::make_pair(first + dx[k], second + dy[k])); 
                            map[first + dx[k]][second + dy[k]] = '$';
                        } else if (map[first + dx[k]][second + dy[k]] == other_color) {
                            check_surrounded = false; 
                        }
                        if (map[first + dx[k]][second + dy[k]] == self_color) {
                            check_only_wall = true;
                        }

                    }
                }
                if (check_surrounded == true && check_only_wall == true) {
                    total += count;
                } else {
                    check_surrounded = true;
                    check_only_wall = false;
                }
            }
            /*
               for (int a = 0; a <= h + 1; a++) {
               for (int b = 0; b <= w + 1; b++) {
               std::cout << map[a][b];
               }
               std::cout << std::endl;
               }
               std::cout << "COUNT = " << count << std::endl;
               std::cout << "TOTAL = " << total << std::endl;
               std::cout << std::endl;
             */
        }
    }
    return total; 
}

