void dfs( int y, int x )
{
  if( !used[y][x] ) {
    used[y][x] = true;
    cnt++;

    for(int i=0; i<4; i++) {
      int nx = x + dx[i], ny = y + dy[i];

      if( nx < 0 || ny < 0 || nx >= w || ny >= h ) continue;
      if( data[ny][nx] == 'B' ) { B = true; continue; }
      if( data[ny][nx] == 'W' ) { W = true; continue; }

      dfs( ny, nx );
    }
  }
}

main()
{
