using namespace std;

#define MAX 10

char area[MAX][MAX];
int ans[MAX][MAX];
int visit[MAX][MAX];
int W,H;

void init()
{
	fill_n((char *)area, sizeof(area) / sizeof(char), '.');
	fill_n((int *)ans, sizeof(ans) / sizeof(int), 0);
	fill_n((int *)visit, sizeof(visit) / sizeof(int), 0);
}

void clear(){
	fill_n((int *)visit, sizeof(visit) / sizeof(int), 0);
}

void dfs(int x, int y, char color)
{
	int mark = (color == 'W') ? 1 : 2;
	char rival  = (color == 'W') ? 'B':'W';

	int dx[4] = {1,0,-1,0};
	int dy[4] = {0,1,0,-1};

	if(x < 0 || x >= W || y < 0 || y >= H){ return; }
	if(area[y][x] == rival){ return; }

	if(!visit[y][x])
	{
		visit[y][x] = 1;
		if(area[y][x] != 'W' && area[y][x] != 'B'){ ans[y][x] |= mark; }

		rep(i,4){ dfs(x+dx[i], y+dy[i], color); }
	}

	return;
}

int main()
{

	while(cin >> W >> H && W)
	{
		int black=0, white=0;
		init();

		rep(h,H){
			rep(w,W){
				cin >> area[h][w];
			}
		}

		rep(h,H){
			rep(w,W){
				if(area[h][w] != '.'){ 
					clear();
					dfs(w,h,area[h][w]); 
				}
			}
		}

		rep(h,H){
			rep(w,W)
			{
				int num = ans[h][w];
				if(num == 1){ white++; }
				if(num == 2){ black++; }
			}
		}

		cout << black << " " << white << endl;

	}
	return 0;
}

#include <bits/stdc++.h>

using namespace std;
typedef pair<int, int> P;

int main(void) {
  int w, h;
  while (cin >> w >> h, w) {
    vector<string> a(h);
    for (int i = 0; i < h; i++) {
      cin >> a[i];
    }

    bool white[51][51] = {}, black[51][51] = {};
    int di[] = {0, -1, 0, 1}, dj[] = {1, 0, -1, 0};
    for (int i = 0; i < h; i++) {
      for (int j = 0; j < w; j++) {
        if (a[i][j] == 'W') {
          queue<P> que;
          que.push(P(i, j));

          while (!que.empty()) {
            P p = que.front(); que.pop();
            int ci = p.first, cj = p.second;
            for (int k = 0; k < 4; k++) {
              int ni = ci + di[k], nj = cj + dj[k];
              if (ni >= 0 && ni < h && nj >= 0 && nj < w && a[ni][nj] != 'B' && white[ni][nj] == false) {
                que.push(P(ni, nj));
                white[ni][nj] = true;
              }
            }
          }
        } else if (a[i][j] == 'B') {
          queue<P> que;
          que.push(P(i, j));
          while (!que.empty()) {
            P p = que.front(); que.pop();
            int ci = p.first, cj = p.second;
            for (int k = 0; k < 4; k++) {
              int ni = ci + di[k], nj = cj + dj[k];
              if (ni >= 0 && ni < h && nj >= 0 && nj < w && a[ni][nj] != 'W' && black[ni][nj] == false) {
                que.push(P(ni, nj));
                black[ni][nj] = true;
              }
            }
          }
        }
      }
    }

    int ans_w = 0, ans_b = 0;
    for (int i = 0; i < h; i++) {
      for (int j = 0; j < w; j++) {
        if (black[i][j] && white[i][j]) continue;
        if (a[i][j] == '.' && black[i][j]) ans_b++;
        if (a[i][j] == '.' && white[i][j]) ans_w++;
      }
    }

    cout << ans_b << " " << ans_w << endl;
  }

  return 0;
}

