    cin.tie(0);
    ios::sync_with_stdio(false);
    cout << fixed << setprecision(10);

    for (;;) {
        int W, H; cin >> W >> H;
        if (W == 0) break;
        vector<string> a(H);
        REP(i, H) cin >> a[i];

        const int di[] = {0, 1, 0, -1};
        const int dj[] = {1, 0, -1, 0};

        function<void(int,int,vvi&,char)> dfs = [&](int i, int j, vvi& used, char invalid) {
            if (used[i][j]) return;
            used[i][j] = 1;
            REP(k, 4) {
                int ni = i + di[k], nj = j + dj[k];
                if (ni < 0 or nj < 0 or H <= ni or W <= nj or
                    a[ni][nj] == invalid) {
                    continue;
                }
                dfs(ni, nj, used, invalid);
            }
        };

        vvi black(H, vi(W));
        REP(i, H) {
            REP(j, W) {
                if (a[i][j] == 'B') dfs(i, j, black, 'W');
            }
        }
        vvi white(H, vi(W));
        REP(i, H) {
            REP(j, W) {
                if (a[i][j] == 'W') dfs(i, j, white, 'B');
            }
        }

        int b = 0, w = 0;
        REP(i, H) {
            REP(j, W) {
                if (a[i][j] != '.') continue;
                if (black[i][j] and !white[i][j]) ++b;
                if (!black[i][j] and white[i][j]) ++w;
            }
        }
        cout << b << " " << w << endl;
   }

    return 0;
}

