bool outof_range(int h, int w) {
  return h < 0 || W <= h || w < 0 || W <= w;
}

void dfs(int h, int w, char c, char target) {
  if (outof_range(h, w) or field[h][w] != target) {
    return;
  }
  field[h][w] = c;
  REP(dir, 4) {
