	v.assign(a,vector<T>(b,t));
}

//v.erase(unique(v.begin(),v.end()),v.end());

vector<vint> vv;
vector<vint> vis;
int B,W;
int x,y;
bool f1,f2;
int foo(int a,int b){
	int ret=0;
	// cout<<a<<" "<<b<<endl;
	if(vv[a][b]==1) f1=true;
	if(vv[a][b]==2) f2=true;
	if(vis[a][b]) return 0;
	vis[a][b]=1;
	ret++;
	if(a>0){
		ret+=foo(a-1,b);
	}
	if(a<x-1){
		ret+=foo(a+1,b);
	}
	if(b>0){
		ret+=foo(a,b-1);
	}
	if(b<y-1){
		ret+=foo(a,b+1);
	}

	return ret;
}

int main(){
	// int x,y;
	while(cin>>y>>x,x||y){
		initvv(vv,x,y);
		initvv(vis,x,y);
		B=0;
		W=0;
		rep(i,x){
			rep(j,y){
				vis[i][j]=0;
				char t;
				cin>>t;
				if(t=='B'){
					vv[i][j]=1;
					vis[i][j]=1;
				}
				else if(t=='.'){
					vv[i][j]=0;
				}
				else{
					vv[i][j]=2;
					vis[i][j]=1;
				}
				// cout<<vv[i][j];
			}
			// cout<<endl;
		}
		// initvv(vis,x,y);
		rep(i,x){
			rep(j,y){
				if(vis[i][j]==0){
					f1=false;
					f2=false;
					int tt=foo(i,j);
					if(f1&&!f2){
						B+=tt;
					}
					if(!f1&&f2){
						W+=tt;
					}
				}
			}
		}
		cout<<B<<" "<<W<<endl;

	}
	return 0;
}

