#include <iostream>
using namespace std;

int w, h, cnt;
char cmp;
char field[50][50];
int dx[] = { -1, 0, 1, 0 };
int dy[] = { 0, -1, 0, 1 };

bool search( int sx, int sy ){
    cnt++;
    field[sy][sx] = '#';
    bool ans = true;
    for( int i = 0; i < 4; i++ ){
        int x = sx + dx[i];
        int y = sy + dy[i];
        if( 0 <= x && x < w && 0 <= y && y < h && field[y][x] != '#' ){
            if( field[y][x] == 'B' || field[y][x] == 'W' ){
                if( cmp == '.' ) cmp = field[y][x];
                else if( cmp != field[y][x] ) ans =  false;
            }else if( !search( x, y ) ) ans = false;
        }
    }
    return ans;
}

int main(){
    while( cin >> w >> h && w ){
        for( int i = 0; i < h; i++ ){
            for( int j = 0; j < w; j++ ){
                cin >> field[i][j];
            }
        }
        int cnt_b = 0, cnt_w = 0;
        for( int i = 0; i < h; i++ ){
            for( int j = 0; j < w; j++ ){
                cnt = 0;
                cmp = '.';
                if( field[i][j] == '.' ){
                    if( search( j, i ) ){
                        if( cmp == 'B' ) cnt_b += cnt;
                        else if( cmp == 'W' ) cnt_w += cnt;
                    }
                }
            }
        }
        cout << cnt_b << ' ' << cnt_w << endl;
    }
}

#include <bits/stdc++.h>
using namespace std;

typedef pair<int, int> P;

int w, h;

bool check(int x, int y){
	return x >= 0 && x < w && y >= 0 && y < h;	
}


int main() {
	int dx[] = {1, 0, -1, 0};
	int dy[] = {0, 1, 0, -1};
	while(cin >> w >> h && (w || h)){
		string a;
		int cntB = 0, cntW = 0;
		vector<string> maze;
		for(int i = 0; i < h; ++i){
			cin >> a;
			maze.push_back(a);
		}
		for(int y = 0; y < h; ++y){
			for(int x = 0; x < w; ++x){
				if(maze[y][x] != '.')
					continue;
				bool white = false, black = false;
				queue<P> que;
				bool visited[64][64] = {0};
				que.push(P(x, y));
				while(!que.empty()){
					P p = que.front();
					que.pop();
					int px = p.first;
					int py = p.second;
					visited[py][px] = true;
					if(maze[py][px] == 'W'){
						white = true;
						//cout << x << " " << y << endl;
						continue;
					}
					if(maze[py][px] == 'B'){
						black = true;
						continue;
					}
					for(int i = 0; i < 4; ++i){
						int nx = px + dx[i];
						int ny = py + dy[i];
						if(check(nx, ny) && !visited[ny][nx])
							que.push(P(nx, ny));
					}
				}
				if(!white && black)
					cntB++;
				else if(white && !black)
					cntW++;
			}
		}
		cout << cntB << " " << cntW << endl;
	}
	return 0;
}

#include <iostream>
using namespace std;
const int dx[] = {1, 0, -1, 0};
const int dy[] = {0, 1, 0, -1};
enum COLOR {
	BLACK,
	UNKNOWN,
	WHITE,
	BOTH,
};

int w, h;
char field[52][52];

COLOR color;
int count;

void dfs(int x, int y) {
	field[y][x] = '#';
	count++;
	for (int i = 0; i < 4; i++) {
		int nextX = x + dx[i];
		int nextY = y + dy[i];
		if (0 <= nextX && nextX < w && 0 <= nextY && nextY < h && field[nextY][nextX] != '#') {
			switch (field[nextY][nextX]) {
			case '.':
				dfs(nextX, nextY);
				break;
			case 'W':
				if (color == BLACK) color = BOTH;
				else if (color == UNKNOWN) color = WHITE;
				break;
			case 'B':
				if (color == WHITE) color = BOTH;
				else if (color == UNKNOWN) color = BLACK;
				break;
			}
		}
	}
}

void printfield() {
	for (int i = 0; i < h; i++) {
		cout << field[i] << endl;
	}
}

