	while (cin >> w >> h, w || h) {
		for (int i = 0; i < h; i++) {
			cin >> field[i];
		}
		int black = 0, white = 0;
		for (int i = 0; i < h; i++) {
			for (int j = 0; j < w; j++) {
				if (field[i][j] == '.') {
					count = 0;
					color = UNKNOWN;
					dfs(j, i);
					if (color == BLACK) black += count;
					if (color == WHITE) white += count;
//					if (color == UNKNOWN) cout << "unknown: " << count << endl;
