#include <iostream>
#include <vector>

using namespace std;
int dx[] = {-1,  0, 0, 1};
int dy[] = { 0, -1, 1, 0};
int w, h;
vector< vector<char> > Field;
vector< vector<char> > used;
bool Bflag;
bool Wflag;
int Bsum;
int Wsum;

void search(int x, int y)
{
	used[y][x] = true;
	if(Bflag && Wflag)
	{
		return;
	}
	for(int i = 0; i < 4; ++i)
	{
		int nx = x + dx[i];
		int ny = y + dy[i];
		if(Field[ny][nx] == '.' && !used[ny][nx])
		{
			search(nx, ny);
		}
		else if(Field[ny][nx] == 'W')
		{
			Wflag = true;
		}
		else if(Field[ny][nx] == 'B')
		{
			Bflag = true;
		}
	}
}

void sum(int a, int b, char c)
{
	Field[b][a] = '2';
	if(c == 'B')
	{
		++Bsum;
		for(int i = 0; i < 4; ++i)
		{
			int nx = a + dx[i];
			int ny = b + dy[i];
			if(Field[ny][nx] == '.')
			{
				sum(nx, ny, c);
			}
		}		
	}
	else if(c == 'W')
	{
		++Wsum;
		for(int i = 0; i < 4; ++i)
		{
			int nx = a + dx[i];
			int ny = b + dy[i];
			if(Field[ny][nx] == '.')
			{
				sum(nx, ny, c);
			}
		}
	}
}

void invalid(int a, int b, char c)
{
	Field[b][a] = c;
	for(int i = 0; i < 4; ++i)
	{
		int nx = a + dx[i];
		int ny = b + dy[i];
		if(Field[ny][nx] == '.')
		{
			invalid(nx, ny, c);
		}
	}
}

void solve()
{
	while(cin >> w >> h, w || h)
	{
		Field.resize(h + 2);
		used.resize(h + 2);
		for(int i = 0; i <= h + 1; ++i)
		{
			Field[i].resize(w + 2);
			used[i].resize(w + 2);
		}
		for(int i = 0; i <= h + 1; ++i)
		{
			for(int j = 0; j <=  w + 1; ++j)
			{
				Field[i][j] = '2';
				used[i][j] = false;
			}
		}
		for(int i = 1; i <= h; ++i)
		{
			for(int j = 1; j <= w; ++j)
			{
				cin >> Field[i][j];
			}
		}
		Bsum = 0;
		Wsum = 0;
		for(int i = 1; i <= h; ++i)
		{
			for(int j = 1; j <= w; ++j)
			{
				Bflag = false;
				Wflag = false;
				if(Field[i][j] == '.')
				{
					search(j, i);
					if(Bflag && !Wflag)
					{
						sum(j, i, 'B');
					}
					else if(!Bflag && Wflag)
					{
						sum(j, i, 'W');
					}
					else if(Bflag && Wflag)
					{
						invalid(j, i, '2');
					}
				}
			}
		}
		cout << Bsum << " " << Wsum << endl;
	}
}

int main()
{
	solve();
	return(0);
}

