		int wh = 0;
		rep(i, h){
			rep(j, w){
				if(field[i][j] == '.'){
					int ret = seedfill(j, i);
					if(ret < 0) br += -ret;
					else wh += ret;
				}
			}
		}
		cout << br << ' ' << wh << endl;
	}

	return 0;
}

