      for(int j=0;j<W;j++){
	cin>>BW[i][j];
      }
    }
    for(int i=0;i<H;i++){
      for(int j=0;j<W;j++){
     	if(BW[i][j]=='.'){
	  sum=0;
	  a=judgeW(i,j);
          b=judgeB(i,j);
	  if((a!=0 && b!=0)||(a==0&&b==0))continue;
          else if(a>0)sumw+=sum;
	  else sumb+=sum;
	  }
      }
    }
    cout<<sumb<<" "<<sumw<<endl;
  }
  return 0;
}

